import CircularProgress from '@mui/material/CircularProgress';
import { purple } from '@mui/material/colors';
import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import CommentCard from '../components/CommentCard';
import NavBar from '../components/NavBar'
import httpService from '../services/http.service';

export default function Product() {
    const [product, setProduct] = useState({
        nom: '',
        img: {},
        compagnieRetailer: '',
        prix: '',
        rating: '' ,
        comments: []
      });
    const [isLoading, setIsLoading] = useState(true);
    const urlParams = new URLSearchParams(useLocation().search);
    const productId = urlParams.get('id');

    React.useEffect(async () => {
        setIsLoading(true);
        const fetchedProduct = await httpService.getProductByID(productId);
        setProduct(fetchedProduct);
        setIsLoading(false);
        window.scrollTo(0, 0)
    }, []);

    return (
        <div className='product-container'>
            <NavBar />
            <main>
                {isLoading ? (
                    <div className='loader-container'>
                        <CircularProgress color='success' />
                    </div>
                ) : (
                    <article id='recette-article'>
                        <h1 className='recette-header'>{product.nom}</h1>
                        <div className='row'>
                            <div className='col'>
                                <img className='recette-image' alt={product.nom} src={product.img} />
                            </div>
                            <div className='col'>
                                <p>Company/Retailer: {product.compagnieRetailer}</p>
                                <p>{product.prix}$</p>
                                <p>{product.rating}/5.0</p>
                                <button type='button' className='btn' onClick={window.print} id='add-to-cart-btn'>
                                    Add to cart
                                </button>
                            </div>
                        </div>
                        <h2>Comments</h2>
                        <button type='button' className='btn' onClick={window.print}>
                            Add comment
                        </button>
                        <form className='product-form-group' id='ajouter-recette-form' onSubmit={addRecipe}>
                            <fieldset className='form-control'>
                                <legend>Product Information</legend>

                                <label htmlFor='nom'>Product name:</label>
                                <input type='text' id='nom' value={productInfos.name} onChange={(e) => {handleChange(e)}} required />

                                <label htmlFor='categorie'>Product category:</label>
                                <select id='categorie' value={productInfos.categorie} onChange={(e) => {handleChange(e)}} required>
                                    <option value='' disabled>
                                        Choose
                                    </option>
                                    <option value='Shoes'>Shoes</option>
                                    <option value='Clothes'>Clothes</option>
                                    <option value='Games'>Games</option>
                                    <option value='Sports'>Sports</option>
                                    <option value='Tech'>Tech</option>
                                    <option value='Autre'>Autre</option>
                                </select>

                                <label htmlFor='compagnieRetailer'>Username of the seller (your username):</label>
                                <input type='text' id='compagnieRetailer' value={productInfos.compagnieRetailer} onChange={(e) => {handleChange(e)}} required />

                                <label htmlFor='prix'>Price:</label>
                                <input type='text' id='prix' value={productInfos.prix} onChange={(e) => {handleChange(e)}} required />

                                <label htmlFor='rating'>Rating (out of 5.0):</label>
                                <input type='text' id='rating' value={productInfos.rating} onChange={(e) => {handleChange(e)}} required />

                                <label htmlFor='img'>Product image:</label>
                                <input type='file' id='img' accept='image/*' onChange={(e) => {handleChange(e)}} ref={imgRef} required />
                            </fieldset>
                    
                    <input className='btn' id='add-product-btn' type='submit' value='Add product' />
                </form>
                        <div className='recette-container'>
                            {product.comments.map((comment) => (
                                <CommentCard key={comment.commentIndex} comment={comment} />
                            ))}
                        </div>
                    </article>
                )}
            </main>
        </div>
    )
}
