import React, { useState } from 'react';
import CircularProgress from '@mui/material/CircularProgress';
import httpService from '../services/http.service';
import NavBar from '../components/NavBar'
import CartProductCard from '../components/CartProductCard';
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
// import 'bootstrap/dist/css/bootstrap.min.css';
import 'modalsonly/css/bootstrap-3.2.0.min.css';

export default function Cart() {
    const [cartProducts, setCartProducts] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [cartTotal, setCartTotal] = useState(0.00);
    const [show, setShow] = useState(false);

    React.useEffect(async () => {
        setIsLoading(true);
        setCartProducts(await httpService.fetchAllCartProducts());
        setIsLoading(false);
        updateCartTotal();
    }, []);

    React.useEffect(() => {
        updateCartTotal();
    }, [cartProducts])

    const updateCartTotal = () => {
        cartProducts.forEach((cartProduct) => {
            setCartTotal(cartTotal => cartTotal + parseFloat(cartProduct.prix))
        })
    };

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <div className='home-container'>
            <NavBar />
            <main>
                <article>
                    <div className='cart-infos right-header right-align'>
                        <span className='right-header' id='recettes-count'>
                            You have added {cartProducts.length} product{cartProducts.length > 1 ? 's' : ''} to your cart!
                        </span>
                        <span className='right-header' id='recettes-count'>
                            Cart total: {cartTotal} $
                        </span>
                        <button type='button' className='btn' onClick={handleShow}>
                            Proceed to checkout
                        </button>
                    </div>
                    <h1>Products in your cart</h1>
                    <h2 className='disclaimer'>If you decide to remove items, refresh your page to see changes</h2>
                    <div className='recettes' id='recettes-list'>
                        {isLoading ? (
                            <div className='loader-container'>
                                <CircularProgress color='success' />
                            </div>
                        ) : (
                            cartProducts.map((cartProduct) => <CartProductCard key={cartProduct.id} cartProduct={cartProduct} />)
                        )}
                    </div>
                </article>
            </main>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    )
}
