import CircularProgress from '@mui/material/CircularProgress';
import React, { useState } from 'react'
import NavBar from '../components/NavBar'
import httpService from '../services/http.service';
import { Tokens } from '../Auth_Tokens/Tokens'
import { useHistory } from "react-router-dom";

export default function Home() {
    const [products, setProducts] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [selectedCategory, setSelectedCategory] = useState('');

    let history = useHistory();

    React.useEffect(async () => {
        setProducts(await httpService.fetchAllProducts());
        setIsLoading(false);
    }, []);

    const handleCategoryChange = async (category) => {
        setSelectedCategory(category);
        setIsLoading(true);
        setProducts(await httpService.getProductsByCategory(category));
        setIsLoading(false);
      };

    if (Tokens.users[0].user1.user1AuthToken) {
        return (
            <div>
                <NavBar />
                <main>
                    <section className='recettes-type' aria-label='Filtres'>
                        <h2>Catégories</h2>
                            <CategoryButtonGroup selectedCategory={selectedCategory} handleClick={handleCategoryChange} />
                    </section>
                    <article>
                        <span className='right-header' id='recettes-count'>
                            {products.length} product{products.length > 1 ? 's' : ''}
                        </span>
                        <h1>Products</h1>
                        <div className='recettes' id='recettes-list'>
                            {/* TODO : Ajouter le component RecipeCard pour chaque recette si isLoading est à false */}
                            {isLoading ? (
                                <div className='loader-container'>
                                    <CircularProgress color='success' />
                                </div>
                            ) : (
                              products.map((product) => <RecipeCard key={recipe.id} recipe={recipe} />)
                            )}
                        </div>
                    </article>
                </main>
            </div>
        )
    }
    else {
        history.push("/nope");
        return (null)
    }
}
