import CircularProgress from '@mui/material/CircularProgress';
import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import NavBar from '../components/NavBar'
import httpService from '../services/http.service';

export default function Produit() {
    const [product, setProduct] = useState({
        nom: '',
        img: {},
        compagnieRetailer: '',
        prix: '',
        rating: '' ,
        comments: []
      });
    const [isLoading, setIsLoading] = useState(true);
    // const urlParams = new URLSearchParams(useLocation().search);
    // const productId = urlParams.get('id');

    // React.useEffect(async () => {
    //     console.log(productId)
    //     setIsLoading(true);
    //     const fetchedProduct = await httpService.getProductByID(productId);
    //     setProduct(fetchedProduct);
    //     setIsLoading(false);
    // }, []);

    React.useEffect(() => {
        const urlParams = new URLSearchParams(useLocation().search);
        const productId = urlParams.get('id');
        console.log(productId)
    }, [])

    return (
        <div>
            <NavBar />
            <main>
                {isLoading ? (
                    <div className='loader-container'>
                        <CircularProgress color='success' />
                    </div>
                ) : (
                    <article id='recette-article'>
                        <h1 className='recette-header'>{product.nom}</h1>
                        <button type='button' className='btn' onClick={window.print} id='print-btn'>
                            Imprimer
                        </button>
                        <div className='row'>
                            <div className='col'>
                                <img className='recette-image' alt={product.nom} src={product.img} />
                            </div>
                            <div className='col'>
                            </div>
                        </div>
                        <h1>Les étapes à suivre pour une recette réussite!</h1>
                        <div className='recette-container'>
                            {/* TODO : Ajouter le component StepCard pour chaque étape de la recette */}
                            {/* {product.comments.map((step) => (
                                <StepCard key={step.ordre} step={step} />
                            ))} */}
                        </div>
                    </article>
                )}
            </main>
        </div>
    )
}
