import CircularProgress from '@mui/material/CircularProgress';
import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import CommentCard from '../components/CommentCard';
import NavBar from '../components/NavBar'
import httpService from '../services/http.service';
import { useHistory } from "react-router-dom";

export default function Product() {
    const [commentInfos, setCommentInfos] = useState({
        // productId: productId,
        user: '',
        comment: ''
    });
    const [product, setProduct] = useState({
        nom: '',
        img: {},
        compagnieRetailer: '',
        prix: '',
        rating: '' ,
        comments: []
      });
    const [isLoading, setIsLoading] = useState(true);
    const urlParams = new URLSearchParams(useLocation().search);
    const productId = urlParams.get('id');
    let history = useHistory();

    React.useEffect(async () => {
        setIsLoading(true);
        const fetchedProduct = await httpService.getProductByID(productId);
        setProduct(fetchedProduct);
        setIsLoading(false);
        window.scrollTo(0, 0)
    }, []);

    // const handleChange = (e) => {
    //     const commentInfosCopy = { ...commentInfos };
    //     commentInfosCopy[e.target.id] = e.target.value;
    //     setCommentInfos(commentInfosCopy);
    // };

    const addComment = async (event) => {
        event.preventDefault();
        // await productFormService.submitForm(productInfos);
        window.location.reload(false);
        history.push("/nope");
    };

    return (
        <div className='product-container'>
            <NavBar />
            <main>
                {isLoading ? (
                    <div className='loader-container'>
                        <CircularProgress color='success' />
                    </div>
                ) : (
                    <article id='recette-article'>
                        <h1 className='recette-header'>{product.nom}</h1>
                        <div className='row'>
                            <div className='col'>
                                <img className='recette-image' alt={product.nom} src={product.img} />
                            </div>
                            <div className='col'>
                                <p>Company/Retailer: {product.compagnieRetailer}</p>
                                <p>{product.prix}$</p>
                                <p>{product.rating}/5.0</p>
                                <button type='button' className='btn' onClick={window.print} id='add-to-cart-btn'>
                                    Add to cart
                                </button>
                            </div>
                        </div>
                        <h2>Comments</h2>
                        {/* <form onSubmit={addComment}>
                            <fieldset>
                                <label htmlFor='user'>User (your username):</label>
                                <input type='text' id='user' value={commentInfos.user} onChange={(e) => {handleChange(e)}} required />

                                <label htmlFor='comment'>Comment:</label>
                                <input type='text' id='comment' value={commentInfos.comment} onChange={(e) => {handleChange(e)}} required />
                            </fieldset>
                            <input className='btn' id='add-product-btn' type='submit' value='Add comment' />
                        </form> */}
                        <div className='recette-container'>
                            {product.comments.map((comment) => (
                                <CommentCard key={comment.commentIndex} comment={comment} />
                            ))}
                        </div>
                    </article>
                )}
            </main>
        </div>
    )
}
