import React from 'react';
import { Route, Switch } from 'react-router-dom';
import Login from './screens/Login';
import Admin from './screens/Admin';
import Vendeur from './screens/Vendeur';
import Contact from './screens/Contact.jsx';
import Erreur from './screens/Erreur';
import Produit from './screens/Produit';
import Produits from './screens/Produits';
import './style.css';

function App() {

  async function checkPassword() {
    try {
      // const response = await fetch('http://localhost:5000/api/checkpassword', {
      //   method: 'POST',
      //   headers: {
      //     'Content-Type': 'application/json'
      //   },
      //   body: JSON.stringify({'id': 1234}) // body data type must match "Content-Type" header 
      // })
      const response = await fetch('http://localhost:5000/api/checkpassword', {
        method: 'POST', // *GET, POST, PUT, DELETE, etc.
        mode: 'cors', // no-cors, *cors, same-origin
        cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
        credentials: 'same-origin', // include, *same-origin, omit
        headers: {
          'Content-Type': 'application/json'
        // 'Content-Type': 'application/x-www-form-urlencoded',
        },
        redirect: 'follow', // manual, *follow, error
        referrerPolicy: 'no-referrer', // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
        body: JSON.stringify({'id': 1234}) // body data type must match "Content-Type" header 
      })//.then(res => res.json())
      const ready = await response.json()
      return ready
    } catch (error) { 
      console.log(error) 
    }

  }
  return (
    <Switch>
      <Route exact path='/'><Login authorized={checkPassword} /></Route>
      {/* <Route exact path='/' component={Login} /> */}
      <Route exact path='/produits'><Produits /></Route>
      <Route exact path='/produit' component={Produit} />
      <Route exact path='/contact' component={Contact} />
      <Route exact path='/vendeur' component={Vendeur} />
      <Route exact path='/admin' component={Admin} />
      <Route path='*' component={Erreur} />
    </Switch>
  );
}

export default App;
