import httpService from './http.service';

class AddCartService {
  constructor() {
    this.httpService = httpService;
  }

  async addProductToCart(product) {
    const comment = {
        nom: productInfos.nom,
        src: '',
        compagnieRetailer: productInfos.compagnieRetailer,
        img: await this.getImageFile(productInfos.img),
        prix: productInfos.prix
    };

    this.httpService.addNewComment(comment);
  }
}

const addCartService = new AddCartService();
export default addCartService;
