import React, { useState } from 'react'
import httpService from '../services/http.service';

export default function Checkout() {
    const [cartProducts, setCartProducts] = useState([]);
    const [cartTotal, setCartTotal] = useState(0.00);

    React.useEffect(async () => {
        setCartProducts(await httpService.fetchAllCartProducts());
        updateCartTotal();
    }, []);

    const updateCartTotal = () => {
        cartProducts.forEach((cartProduct) => {
            setCartTotal(cartTotal => cartTotal + parseFloat(cartProduct.prix))
        })
    };
    return (
        <div>
            <article>
                <h1>Enter your payment information</h1>
                {/* <h2 className='disclaimer'>Make sure to resign-in after adding a product to the market!</h2> */}
                <br />
                <h2>Your cart products:</h2>
                <div>
                    {cartProducts.map((cartProduct) => (
                        <p key={cartProduct.id}>{cartProduct.nom}</p>
                    ))}
                </div>
                <h2>Your cart total is {cartTotal}</h2>
                <form className='product-form-group' id='ajouter-recette-form' onSubmit={addRecipe}>
                    <fieldset className='form-control'>
                        <label htmlFor='categorie'>Choose a payment method:</label>
                        <select id='categorie' required>
                            <option value='' disabled>
                                Choose
                            </option>
                            <option value='Credit'>Credit</option>
                            <option value='Debit'>Debit</option>
                        </select>

                        <label htmlFor='nom'>Card number:</label>
                        <input type='text' id='nom' required />

                        <label htmlFor='nom'>Card experation date:</label>
                        <input type='text' id='nom' required />

                        <label htmlFor='compagnieRetailer'>Username of the seller (your username):</label>
                        <input type='text' id='compagnieRetailer' value={productInfos.compagnieRetailer} onChange={(e) => {handleChange(e)}} required />

                        <label htmlFor='prix'>Price:</label>
                        <input type='text' id='prix' value={productInfos.prix} onChange={(e) => {handleChange(e)}} required />

                        <label htmlFor='rating'>Rating (out of 5.0):</label>
                        <input type='text' id='rating' value={productInfos.rating} onChange={(e) => {handleChange(e)}} required />

                        <label htmlFor='img'>Product image:</label>
                        <input type='file' id='img' accept='image/*' onChange={(e) => {handleChange(e)}} ref={imgRef} required />
                    </fieldset>
                    
                    <input className='btn' id='add-product-btn' type='submit' value='Add product' />
                </form>
                </article>
        </div>
    )
}
