import React, { useState } from 'react'
import Rating from '@mui/material/Rating';
import NavBar from '../components/NavBar';

export default function AddProduct() {
    const [productInfos, setProductInfos] = useState({
        nom: '',
        img: {},
        compagnieRetailer: '',
        categorie: '',
        prix: '',
        rating: '' ,
        comments: []
    });
    const imgRef = React.createRef();

    const handleChange = (e) => {
        const productInfosCopy = { ...productInfos };
        productInfosCopy[e.target.id] = e.target.id === 'img' ? imgRef.current.files[0] : e.target.value;
        setProductInfos(productInfosCopy);
    };

    const addRecipe = async (event) => {
        event.preventDefault();
        // await recipeFormService.submitForm(recipeInfos, steps);
        window.location.reload(false);
    };

    return (
        <div className='add-product-container'>
            <NavBar />
            <main>
                <article>
                <h1>Ajouter votre propre recette!</h1>
                <br />

                <form className='form-group' id='ajouter-recette-form' onSubmit={addRecipe}>
                    <fieldset className='form-control'>
                        <legend>Ajouter les informations de la recette</legend>

                        <label htmlFor='productName'>Product name:</label>
                        <input type='text' id='name' value={productInfos.name} onChange={(e) => {handleChange(e)}} required />

                        <label htmlFor='type'>Sélectionner le type de recette:</label>
                        <select id='type' value={productInfos.categorie} onChange={(e) => {handleChange(e)}} required>
                            <option value='' disabled>
                                Choose
                            </option>
                            <option value='Shoes'>Végétarienne</option>
                            <option value='Clothes'>Méditérranéenne</option>
                            <option value='Games'>Keto</option>
                            <option value='Sports'>Keto</option>
                            <option value='Tech'>Keto</option>
                            <option value='autre'>Autre</option>
                        </select>

                        <label htmlFor='seller'>Username of the seller (your username):</label>
                        <Rating name="half-rating" defaultValue={2.5} precision={0.1} />
                        <input type='text' id='time' value={productInfos.compagnieRetailer} onChange={(e) => {handleChange(e)}} required />

                        <label htmlFor='price'>Price:</label>
                        <input type='text' id='time' value={productInfos.prix} onChange={(e) => {handleChange(e)}} required />

                        <label htmlFor='rating'>Rating (out of 5.0):</label>
                        <input type='text' id='time' value={productInfos.rating} onChange={(e) => {handleChange(e)}} required />

                        <label htmlFor='img'>Product image:</label>
                        <input type='file' id='img' accept='image/*' onChange={(e) => {handleChange(e)}} ref={imgRef} required />
                    </fieldset>
                    
                    <input className='btn' type='submit' value='Add product' />
                </form>
                </article>
            </main>
        </div>
    )
}
