import React from 'react'


function Erreur() {
    return (
        <div className='error-container'>
            You've likely posted a comment, removed a product from your cart, added a product to the market or refreshed a page.
            In that case we log you automatically out! Please log back in.
            <button type='button' className='btn' onClick={() => {addProductToCart()}}>
                Login
            </button>
        </div>
    )
}

export default Erreur
