import httpService from './http.service';

class CommentFormService {
  constructor() {
    this.httpService = httpService;
  }

  async submitForm(commentInfos) {
    const comment = {
      productId: productId,
      user: commentInfos.user,
      comment: commentInfos.comment
    };

    this.httpService.addNewProduct(product);
  }

  async getImageFile(file) {
    const image = await new Promise((resolve) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result);
      reader.readAsDataURL(file);
    });

    return image;
  }
}

const productFormService = new ProductFormService();
export default productFormService;
