import httpService from './http.service';

class AddCartService {
  constructor() {
    this.httpService = httpService;
  }

  async addProductToCart(product) {
    const comment = {
        nom: product.nom,
        src: '',
        compagnieRetailer: product.compagnieRetailer,
        img: await this.getImageFile(product.img),
        prix: product.prix
    };

    this.httpService.addProductToCart(comment);
  }

  async getImageFile(file) {
    const image = await new Promise((resolve) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result);
      reader.readAsDataURL(file);
    });

    return image;
  }
}

const addCartService = new AddCartService();
export default addCartService;
