import React, { useState } from 'react'
import NavBar from '../components/NavBar';
import httpService from '../services/http.service';

export default function Checkout() {
    const [cartProducts, setCartProducts] = useState([]);
    const [cartTotal, setCartTotal] = useState(0.00);

    React.useEffect(async () => {
        setCartProducts(await httpService.fetchAllCartProducts());
        updateCartTotal();
    }, []);

    const updateCartTotal = () => {
        cartProducts.forEach((cartProduct) => {
            setCartTotal(cartTotal => cartTotal + parseFloat(cartProduct.prix))
        })
    };

    const sendOrder = () => {

    }

    return (
        <div className='home-container'>
            <NavBar>
                <article>
                <h1>Enter your payment information</h1>
                {/* <h2 className='disclaimer'>Make sure to resign-in after adding a product to the market!</h2> */}
                <br />
                <h2>Your cart products:</h2>
                <div>
                    {cartProducts.map((cartProduct) => (
                        <p key={cartProduct.id}>{cartProduct.nom}</p>
                    ))}
                </div>
                <h2>Your cart total is {cartTotal}</h2>
                <form className='product-form-group' id='ajouter-recette-form'>
                    <fieldset className='form-control' onSubmit={sendOrder}>
                        <label htmlFor='method'>Choose a payment method:</label>
                        <select id='method' required>
                            <option value='' disabled>
                                Choose
                            </option>
                            <option value='Credit'>Credit</option>
                            <option value='Debit'>Debit</option>
                        </select>

                        <label htmlFor='nom'>Card number:</label>
                        <input type='text' id='number' required />

                        <label htmlFor='nom'>Card experation date:</label>
                        <input type='text' id='experation' required />

                        <label htmlFor='nom'>Card security number:</label>
                        <input type='text' id='security' required />

                        <label htmlFor='nom'>Billing address:</label>
                        <input type='text' id='billing' required />
                    </fieldset>
                    
                    <input className='btn' id='add-product-btn' type='submit' value='Cancel' />
                    <input className='btn' id='add-product-btn' type='submit' value='Confirm Order' />
                </form>
            </article>
            </NavBar>
            
        </div>
    )
}
